def get_solar_term_time(year: int, term_index: int) -> Optional[datetime]:
    """
    获取指定年份指定节气的精确时刻
    
    Args:
        year: 年份
        term_index: 节气索引 (0=小寒, 1=立春, 2=惊蛰, ..., 11=大雪)
        
    Returns:
        节气时刻的datetime对象，如果没有数据则返回None
    """
    if year not in SOLAR_TERM_TIMES:
        return None
    
    times = SOLAR_TERM_TIMES[year]
    if term_index < 0 or term_index >= len(times):
        return None
    
    month, day, hour, minute = times[term_index]
    return datetime(year, month, day, hour, minute)


def get_jie_qi_by_date(dt: datetime) -> tuple[str, int]:
    """
    根据日期获取当前所在的节气及月份
    
    Args:
        dt: 日期时间
        
    Returns:
        (节气名称, 农历月份)
    """
    year = dt.year
    
    # 获取当年的节气时刻
    if year not in SOLAR_TERM_TIMES:
        # 如果没有精确数据，使用近似计算
        month = dt.month
        if month == 1:
            return ('小寒', 12) if dt.day < 20 else ('大寒', 12)
        elif month == 2:
            return ('立春', 1) if dt.day >= 4 else ('大寒', 12)
        # ... 简化处理
        return ('未知', 0)
    
    # 查找当前处于哪个节气之后
    times = SOLAR_TERM_TIMES[year]
    current_term_idx = 0
    
    for i, (m, d, h, mi) in enumerate(times):
        term_dt = datetime(year, m, d, h, mi)
        if dt >= term_dt:
            current_term_idx = i
        else:
            break
    
    # 节气名称（交替排列：节-气-节-气...）
    # 小寒(节)、大寒(气)、立春(节)、雨水(气)...
    term_names = ['小寒', '立春', '惊蛰', '清明', '立夏', '芒种',
                  '小暑', '立秋', '白露', '寒露', '立冬', '大雪']
    
    term_name = term_names[current_term_idx]
    lunar_month = JIE_QI_MONTH.get(term_name, current_term_idx + 1)
    
    return (term_name, lunar_month)


def get_month_ganzhi_index(year: int, month: int, day: int, hour: int = 12) -> int:
    """
    获取月柱干支索引（用于八字计算）
    
    根据节气确定月份
    """
    dt = datetime(year, month, day, hour)
    _, lunar_month = get_jie_qi_by_date(dt)
    return lunar_month
